(39, 'wc-admin-wc-helper-connection', 'info', 'en_US', 'Connect to WooCommerce.com', 'Connect to get important product notifications and updates.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-11 11:23:47', NULL, 0, 'plain', '', 0, 0, 'info'),
(40, 'wc-admin-customizing-product-catalog', 'info', 'en_US', 'How to customize your product catalog', 'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-11 11:24:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(41, 'gla-reconnect-wordpress', 'info', 'en_US', 'Re-connect your store to Google Listings & Ads', 'Your WordPress.com account has been disconnected from Google Listings &amp; Ads. Connect your WordPress.com account again to ensure your products stay listed on Google through the Google Listings &amp; Ads extension.<br /><br />If you do not re-connect, any existing listings may be removed from Google.', '{}', 'unactioned', 'gla', '2022-09-11 11:29:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(42, 'gla-before-campaign-migration', 'info', 'en_US', 'Your Google Listings & Ads campaigns will soon be automatically upgraded', 'From July through September, Google will be upgrading your existing campaigns from Smart Shopping to Performance Max, giving you the same benefits, plus expanded reach. There will be no impact to your spend or campaign settings due to this upgrade.', '{}', 'unactioned', 'gla', '2022-09-11 11:29:52', NULL, 0, 'plain', '', 0, 0, 'info'),
(43, 'wc-admin-orders-milestone', 'info', 'en_US', 'First order received', 'Congratulations on getting your first order! Now is a great time to learn how to manage your orders.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-11 12:24:56', NULL, 0, 'plain', '', 0, 0, 'info'),
(44, 'wc-admin-launch-checklist', 'info', 'en_US', 'Ready to launch your store?', 'To make sure you never get that sinking \"what did I forget\" feeling, we\'ve put together the essential pre-launch checklist.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-12 13:54:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(45, 'wc-admin-choosing-a-theme', 'marketing', 'en_US', 'Choosing a theme?', 'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-12 13:54:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(46, 'wc-admin-insight-first-product-and-payment', 'survey', 'en_US', 'Insight', 'More than 80% of new merchants add the first product and have at least one payment method set up during the first week.<br><br>Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-09-12 13:54:11', NULL, 0, 'plain', '', 0, 0, 'info'),
(47, 'wc-admin-mobile-app', 'info', 'en_US', 'Install Woo mobile app', 'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.', '{}', 'unactioned', 'woocommerce-admin', '2022-09-13 12:50:03', NULL, 0, 'plain', '', 0, 0, 'info'),
(48, 'wc-admin-marketing-jetpack-backup', 'marketing', 'en_US', 'Protect your WooCommerce Store with Jetpack Backup.', 'Store downtime means lost sales. One-click restores get you back online quickly if something goes wrong.', '{}', 'unactioned', 'woocommerce-admin-notes', '2022-09-14 13:37:54', NULL, 0, 'thumbnail', 'http://localhost/r-shop/wp-content/plugins/woocommerce/assets/images/admin_notes/marketing-jetpack-2x.png', 0, 0, 'info'),
(49, 'custom_deposit_schedules_inbox', 'marketing', 'en_US', 'Customize your deposit schedule', 'Receive your funds according to your own business needs with custom deposit schedules from WooCommerce Payments. You’ll have the flexibility to deposit funds into your bank account daily, weekly, monthly, or even on-demand – you’ve done the work, now get paid on your terms.', '{}', 'pending', 'woocommerce.com', '2022-09-17 22:44:19', NULL, 0, 'plain', '', 0, 0, 'info'),
(50, 'wc-admin-insight-first-sale', 'survey', 'en_US', 'Did you know?', 'A WooCommerce powered store needs on average 31 days to get the first sale. You\'re on the right track! Do you find this type of insight useful?', '{}', 'unactioned', 'woocommerce-admin', '2022-09-18 12:22:28', NULL, 0, 'plain', '', 0, 0, 'info');
